import React from "react";
import CoinsPresenter from "./CoinsPresenter";
import { getCoins, getCoinById } from "../../api";

export default class extends React.Component {
  state = {
    loading: true,
    coins: [],
    coin: null,
    coin_id: null,
    is_exchanges: false,
    is_markets: false,
  };
  getCoins = async () => {
    try {
      const { data: coins } = await getCoins();

      this.setState({
        coins,
      });
    } catch (e) {
      console.log(e);
    } finally {
      this.setState({ loading: false });
    }
  };

  getCoinById = async () => {
    try {
      const {
        match: {
          params: { id: coin_id },
        },
      } = this.props;

      if (coin_id) {
        const { data: coin } = await getCoinById(coin_id);
        console.log(coin);
        this.setState({
          coin,
        });
      }
    } catch (e) {
      console.log(e);
    } finally {
      this.setState({ loading: false });
    }
  };

  componentDidMount() {
    const {
      match: {
        path,
        params: { id },
      },
    } = this.props;

    if (id) {
      if (path.includes("/exchanges")) {
        console.log("exchanges");
      } else if (path.includes("/markets")) {
        console.log("marckes");
      } else {
        this.getCoinById();
      }
      console.log(this.state);
    } else {
      this.getCoins();
    }
  }
  render() {
    return <CoinsPresenter {...this.state} />;
  }
}
