import React from "react";
import CoinsPresenter from "./CoinsPresenter";
import { getCoins, getCoinById } from "../../api";

export default class extends React.Component {
  state = {
    loading: true,
    coins: [],
    coin: null,
    coin_id: null,
    is_exchanges: false,
    is_markets: false
  };
  getCoins = async () => {
    try {
      const { data: coins } = await getCoins();

      this.setState({
        coins
      });
    } catch (e) {
      console.log(e);
    } finally {
      this.setState({ loading: false });
    }
  };

  getCoinById = async () => {
    try {
      const { coin_id, coin } = this.state;

      if (coin_id && !coin) {
        const {data: coin} = await getCoinById(coin_id);
        this.setState({
          coin
        })
      }
    } catch (e) {
      console.log(e);
    } finally {
      this.setState({ loading: false });
    }
  };

  componentDidMount() {
    const {
      match: {
        path,
        params: { id }
      }
    } = this.props;

    if (id) {
      this.setState({
        coin_id: id,
        is_exchanges: path.includes("/exchanges"),
        is_markets: path.includes("/markets")
      });
      this.getCoinById();
    } else {
      this.getCoins();
    }
  }
  render() {
    return <CoinsPresenter {...this.state} />;
  }
}
