/* name / symbol
  description

  Rank:
  OpenSource:
  Proof Type:
  Structure:
*/

import React from "react";
import styled from "styled-components";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";

const Container = styled.div``;
const Title = styled.h1``;
const LinkContainer = styled.ul`
  display: flex;
  width: 20%;
  height: 30px;
`;

const Linker = styled.li`
  width: 40%;
  height: 100%;

  border-radius: 10%;
  color: ${(props) => (props.selected ? "white" : "black")};
  background-color: ${(props) => (props.selected ? "#1d966e" : "white")};
  border: 2px #1d966e solid;
`;

const CoinDetail = ({
  name,
  symbol,
  description,
  rank,
  open_source,
  proof_type,
  org_structure,
}) => {
  return (
    <Container>
      <Title>
        {name} / {symbol}
      </Title>
      <p>{description}</p>
      <br />
      <p>
        <strong>Rank: </strong>
        {rank}
      </p>
      <p>
        <strong>OpenSource: </strong>
        {open_source ? "true" : "false"}
      </p>
      <p>
        <strong>Proof Type: </strong>
        {proof_type}
      </p>
      <p>
        <strong>Structure: </strong>
        {org_structure}
      </p>
    </Container>
  );
};

CoinDetail.propTypes = {
  name: PropTypes.string.isRequired,
  symbol: PropTypes.string.isRequired,
  description: PropTypes.string.isRequired,
  rank: PropTypes.number,
  open_source: PropTypes.bool.isRequired,
  proof_type: PropTypes.string.isRequired,
  org_structure: PropTypes.string.isRequired,
};

export default CoinDetail;
